import React from 'react'
import "./Widget.scss"
import AttachMoneySharpIcon from '@mui/icons-material/AttachMoneySharp';
import ArrowUpwardOutlinedIcon from '@mui/icons-material/ArrowUpwardOutlined';
import AddShoppingCartOutlinedIcon from '@mui/icons-material/AddShoppingCartOutlined';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import ShoppingBagOutlinedIcon from '@mui/icons-material/ShoppingBagOutlined';
import SouthOutlinedIcon from '@mui/icons-material/SouthOutlined';

const earningsData = ["$198k", "$2.4k", "$2.4k", "$89k"];
const percentagelist = ["37.8%", "2%", "2%", "11%"];
const iconStyle = {
    color: '#8b008b',
    backgroundColor: '#FF77FF', // Light Magenta hex code
    width: '100px', // Adjust the size as needed
    height: '100px',
    borderRadius: '50%', // Makes the container a circle
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    marginLeft: '40px',
  };


function Widget({type}) {

  let data;
  switch(type){
    case "earning":
      data={
        ic:<AttachMoneySharpIcon className='ic'/>,
        title:"Earnings",
        m: earningsData[0],
        percentage:percentagelist[0],
        ip: <ArrowUpwardOutlinedIcon className='ic'/>

      };
      break;
      case "orders":
      data={
        ic:<AddShoppingCartOutlinedIcon
        className='ic'
        style={{ color: "#8b008b", backgroundColor: "#FF77FF" }}
      />,
        title:"Orders",
        m: earningsData[1],
        percentage:(
          <span style={{ color: "red" }}>{percentagelist[1]}</span>
        ),
        ip: <SouthOutlinedIcon className='ic' style={{color: "red"}}/>


      };
      break;
      case "balance":
      data={
        ic:<AccountBalanceWalletIcon className='ic'/>,
        title:"Balance",
        m: earningsData[2],
        percentage:percentagelist[2],
        ip: <SouthOutlinedIcon className='ic' style={{color: "red"}}/>

      };
      break;
      case "total sales":
      data={
        ic:<ShoppingBagOutlinedIcon className='ic'/>,
        title:"Total Sales",
        m: earningsData[3],
        percentage:percentagelist[3],
        ip: <ArrowUpwardOutlinedIcon className='ic'/>

      };
      break;
      default:
        break;
  }
  return (
    <div className='widget'><div className='left'>{data.ic}</div>
    <div className='right'><span className='title'>{data.title}</span>
    <span className='m'>{data.m}</span>
    <span className='percentage positive'>{data.ip}37.18% <span className='ig'>this month</span></span> 
    </div>

    </div>
  )
}

export default Widget
