import React, { useEffect, useRef, useState } from 'react';
import "./barchart.scss";
import Chart from 'chart.js/auto';

function Barchart() {
  const chartRef = useRef(null);
  const [view, setView] = useState('monthly'); // State for the selected view

  const monthlyData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sept', 'Oct', 'Nov', 'Dec'],
    datasets: [
      {
        label: 'Monthly Earnings',
        data: [2000, 3000, 2500, 3500, 4000, 2800, 4500,1000,2000,3000.1000],
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        hoverBackgroundColor: 'rgba(75, 192, 192, 0.5)',
        borderWidth: 0,
      },
    ],
  };

  const quarterlyData = {
    labels: ['Q1', 'Q2', 'Q3', 'Q4'],
    datasets: [
      {
        label: 'Quarterly Earnings',
        data: [7500, 10800, 11300, 7800],
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        hoverBackgroundColor: 'rgba(75, 192, 192, 0.5)',
        borderWidth: 0,
      },
    ],
  };

  const options = {
    scales: {
      x: {
        display: false,
      },
      y: {
        display: false,
      },
    },
    plugins: {
      legend: {
        display: false,
      },
    },
  };

  useEffect(() => {
    if (chartRef && chartRef.current) {
      const context = chartRef.current.getContext('2d');
      const dataToUse = view === 'monthly' ? monthlyData : quarterlyData;
      const newChart = new Chart(context, {
        type: 'bar',
        data: dataToUse,
        options: options,
      });

      return () => {
        newChart.destroy();
      };
    }
  }, [view]);

  return (
    <div className='barchart'>
      <div className="chart-options">
        <select className="chart-dropdown" onChange={(e) => setView(e.target.value)}>
          <option value="monthly">Monthly</option>
          <option value="quarterly">Quarterly</option>
        </select>
      </div>
      <h2>Overview</h2>
      <div className="chart1" style={{ height: '350px' }}>
        <canvas ref={chartRef}></canvas>
      </div>
    </div>
  );
}

export default Barchart;
